import React from 'react'
import User from './Users'

class Users extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: [
                {
                    id: 1,
                    firstname: 'Bob',
                    lastname: 'Marley',
                    bio: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis tortor eros, consequat id lobortis et, elementum sit amet quam. Nulla eget sem ac massa hendrerit pretium et quis mi. Proin efficitur, augue vel viverra suscipit, lorem nisl dignissim mauris, quis ultrices dui libero consequat diam. Nunc est sapien, ultrices vitae imperdiet et, suscipit et odio.',
                    age: 40,
                    isHappy: true
                },
                {   
                    id: 2,
                    firstname: 'John',
                    lastname: 'Doe',
                    bio: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis tortor eros, consequat id lobortis et, elementum sit amet quam. Nulla eget sem ac massa hendrerit pretium et quis mi. Proin efficitur, augue vel viverra suscipit, lorem nisl dignissim mauris, quis ultrices dui libero consequat diam. Nunc est sapien, ultrices vitae imperdiet et, suscipit et odio.',
                    age: 22,
                    isHappy: false
                }
            ]
        }
    }
    render () {
        if (this.state.users.length > 0)
        {
            return (
                <div>
                    {this.state.users.map((el) => (
                        <User key={el.id} user={el}/>
                    ))}
                </div>
            )
        }
        else {
            return (<div className="user">
                    <h3>пользователей нет</h3>
                </div>
            )           
        }
    }
}

export default Users
